mapper.select:
SELECT member_id, member_email, member_pass, member_name, member_phone,
       member_role, member_status, created_at, updated_at
FROM MEMBER WHERE member_email = #{memberEmail}

SELECT member_id, member_email, member_pass, member_name, member_phone,
       member_role, member_status, created_at, updated_at
FROM MEMBER WHERE member_id = #{memberId}

mapper.update:
UPDATE MEMBER SET member_name = #{memberName}, member_phone = #{memberPhone},
       member_address = #{memberAddress}, updated_at = CURRENT_TIMESTAMP
WHERE member_id = #{memberId}
