mapper.select:
SELECT n.news_id, n.stock_id, n.stock_code, (SELECT COALESCE(stock_name, name_ko, name_en, ticker, symbol, stock_code) FROM stock s WHERE s.stock_code = n.stock_code LIMIT 1) as stock_name, n.title, n.link, n.content, n.source, n.country, n.published_at, n.created_at, n.updated_at FROM news n WHERE 1=1
AND ( n.title LIKE CONCAT('%', #{keyword}, '%') OR n.content LIKE CONCAT('%', #{keyword}, '%') OR n.stock_code LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY n.created_at DESC LIMIT #{limit}
