mapper.update:
UPDATE portfolio
SET quantity = #{quantity},
    avg_price = #{avgPrice},
    updated_at = NOW()
WHERE member_id = #{memberId}
  AND stock_code = #{stockCode}

mapper.delete:
DELETE FROM portfolio
WHERE member_id = #{memberId}
  AND stock_code = #{stockCode}

mapper.select:
SELECT COUNT(*)
FROM portfolio
WHERE member_id = #{memberId}

SELECT COALESCE(SUM(p.quantity * p.avg_price), 0) AS total_buy_amount
FROM portfolio
WHERE member_id = #{memberId}
