mapper.insert:
INSERT INTO NEWS (
    stock_id, stock_code, stock_name,
    title, link, content, source, country,
    published_at
) VALUES (
    #{stockId}, #{stockCode}, #{stockName},
    #{title}, #{link}, #{content}, #{source}, #{country},
    #{publishedAt}
)

mapper.select:
SELECT COUNT(*)
FROM NEWS
WHERE link = #{link}
